import { useTranslation } from "react-i18next";

export function WhyChoose() {
  const { t } = useTranslation("about");
  return (
    <div className="py-12 md:py-24 container mx-auto px-4">
      <h5 className="text-xl md:text-5xl font-bold text-center">
        {t("whyChoose.title")}
      </h5>
      <p className="mt-4 text-center text-2xs md:text-base">
        {t("whyChoose.content")}
      </p>
      <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-12">
        <ChooseUsItem
          icon={<LuxuryIcon />}
          title={t("whyChoose.item.item1.title")}
          desc={t("whyChoose.item.item1.desc")}
        />
        <ChooseUsItem
          icon={<DiamonIcon />}
          title={t("whyChoose.item.item2.title")}
          desc={t("whyChoose.item.item2.desc")}
        />
        <ChooseUsItem
          icon={<SettingIcon />}
          title={t("whyChoose.item.item2.title")}
          desc={t("whyChoose.item.item3.desc")}
        />
      </div>
    </div>
  );
}

function ChooseUsItem(props: {
  icon: React.ReactElement;
  title: string;
  desc: string;
}) {
  return (
    <div className="flex flex-col gap-y-2 items-center">
      {props.icon}
      <p className="uppercase font-bold text-sm md:text-xl mt-2">
        {props.title}
      </p>
      <p className="text-xs md:text-base text-center leading-5">{props.desc}</p>
    </div>
  );
}

